import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Dashboard.css';
import { db } from '../firebase/firebase.js';
import { doc, getDoc, setDoc, updateDoc } from 'firebase/firestore'; // Firestore functions

function Dashboard({ userId }) {
  const [selectedTab, setSelectedTab] = useState("My Profile");
  const [profileData, setProfileData] = useState({
    name: "",
    email: "",
    organization: "",
  });
  const [formData, setFormData] = useState({
    sector: "",
    dataSensitivity: "",
    digitalFootprint: "",
    employees: "",
    revenue: "",
    itInvestment: "",
    cyberMaturity: "",
    thirdPartyDependencies: "",
  });
  const [assessmentLogs, setAssessmentLogs] = useState([]);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch user profile and check if "info" and "logs" exist
    const fetchProfileData = async () => {
      try {
        const userRef = doc(db, "users", userId);
        const userSnap = await getDoc(userRef);

        if (userSnap.exists()) {
          const userData = userSnap.data();
          setProfileData({
            name: userData.name || "",
            email: userData.email || "",
            organization: userData.organization || "",
          });

          if (userData.info) {
            setFormData(userData.info);
          }

          if (userData.logs) {
            setAssessmentLogs(userData.logs);
          }
        } else {
          console.error("User data not found!");
        }
      } catch (err) {
        console.error("Error fetching profile data:", err);
      }
    };

    fetchProfileData();
  }, [userId]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async () => {
    // Ensure all fields are filled
    const isFormComplete = Object.values(formData).every(
      (value) => value.trim() !== ""
    );

    if (!isFormComplete) {
      setError("All fields are mandatory. Please fill out all the fields.");
      return;
    }

    setError("");

    try {
      const userRef = doc(db, "users", userId);
      await updateDoc(userRef, { info: formData });
      console.log("User info updated in Firestore");
      alert("Details submitted successfully!");
    } catch (err) {
      console.error("Error updating user info:", err);
    }
  };

  const renderContent = () => {
    switch (selectedTab) {
      case "My Profile":
        return (
          <div className="content">
            <h2>My Profile</h2>
            <div className="profile-info">
              <div className="info-item">
                <span className="info-label">Name:</span>
                <input
                  className="info-input"
                  type="text"
                  value={profileData.name}
                  readOnly
                />
              </div>
              <div className="info-item">
                <span className="info-label">Organization:</span>
                <input
                  className="info-input"
                  type="text"
                  value={profileData.organization}
                  readOnly
                />
              </div>
              <div className="info-item">
                <span className="info-label">Email:</span>
                <input
                  className="info-input"
                  type="email"
                  value={profileData.email}
                  readOnly
                />
              </div>
            </div>
            <div className="questionnaire-section">
              <h3>Organizational Details</h3>
              {error && <div className="error-box">{error}</div>}
              <div>
                <label>Sector of Industry</label>
                <select
                  name="sector"
                  value={formData.sector}
                  onChange={handleInputChange}
                >
                  <option value="">Select Sector</option>
                  {/* Add your options here */}
                </select>
              </div>
              {/* Repeat this block for other fields */}
              <button className="save-btn" onClick={handleSubmit}>
                Submit
              </button>
            </div>
          </div>
        );
      case "Assessment Logs":
        return (
          <div className="content">
            <h2>Assessment Logs</h2>
            {assessmentLogs.length > 0 ? (
              <ul>
                {assessmentLogs.map((log, index) => (
                  <li key={index}>
                    Date: {log.date}, Score: {log.score}, Risk Level: {log.riskLevel}
                  </li>
                ))}
              </ul>
            ) : (
              <p>Take at least one assessment to display logs.</p>
            )}
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="dashboard">
      <aside className="sidebar">
        <div className="sidebar-header">
          <h2>SecuriFi</h2>
        </div>
        <nav>
          <ul>
            <li onClick={() => setSelectedTab("My Profile")}>My Profile</li>
            <li onClick={() => setSelectedTab("Assessment Logs")}>
              Assessment Logs
            </li>
          </ul>
        </nav>
      </aside>
      <div className="main-content">
        <header className="top-navbar">
          <div className="navbar-brand">Dashboard</div>
          <button className="logout-btn">Logout</button>
        </header>
        {renderContent()}
      </div>
    </div>
  );
}

export default Dashboard;
