// src/components/QuestionnairePage.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Questionnaire.css';
import ScorePage from './ScorePage';

function QuestionnairePage() {
  const [data, setData] = useState(null);
  const [currentLayerIndex, setLayerIndex] = useState(0);
  const [currentTopicIndex, setTopicIndex] = useState(0);
  const [currentSubtopicIndex, setSubtopicIndex] = useState(0);
  const [currentSectionIndex, setSectionIndex] = useState(0);  // New state for section
  const [responses, setResponses] = useState({});

  const navigate = useNavigate();

  useEffect(() => {
    fetch('/questionnaireData.json')
      .then(response => response.json())
      .then(data => setData(data))
      .catch(error => console.error("Error loading JSON data:", error));
  }, []);

  if (!data) return <div>Loading...</div>;

  const layer = data.layers[currentLayerIndex];
  const topic = layer.topics[currentTopicIndex];
  const subtopic = topic.subtopics[currentSubtopicIndex];
  const section = subtopic.sections[currentSectionIndex];

  const handleResponseChange = (questionName, answer) => {
    setResponses((prevResponses) => ({
      ...prevResponses,
      [questionName]: answer
    }));
  };

  const handleNext = (answer) => {
    // Update the state based on answer
    if (answer === "Yes") {
      // If answer is Yes, show next question in the current section
      if (currentSectionIndex < subtopic.sections.length - 1) {
        setSectionIndex(currentSectionIndex + 1);  // Move to next section
      } else if (currentSubtopicIndex < topic.subtopics.length - 1) {
        // If no more sections in the current subtopic, move to the next subtopic
        setSectionIndex(0); // Reset section to first
        setSubtopicIndex(currentSubtopicIndex + 1);
      } else if (currentTopicIndex < layer.topics.length - 1) {
        // If no more subtopics, move to the next topic
        setSubtopicIndex(0);
        setTopicIndex(currentTopicIndex + 1);
      } else if (currentLayerIndex < data.layers.length - 1) {
        // If no more topics, move to the next layer
        setSubtopicIndex(0);
        setTopicIndex(0);
        setLayerIndex(currentLayerIndex + 1);
      } else {
        console.log('Questionnaire completed');
        navigate('/score', { state: { responses, jsonData: data } });
      }
    } else {
      // If answer is No, move to the next section in the current subtopic
      if (currentSectionIndex < subtopic.sections.length - 1) {
        setSectionIndex(currentSectionIndex + 1);
      } else {
        // If we have no more sections, move to the next subtopic
        setSectionIndex(0); // Reset section to first
        if (currentSubtopicIndex < topic.subtopics.length - 1) {
          setSubtopicIndex(currentSubtopicIndex + 1);
        } else if (currentTopicIndex < layer.topics.length - 1) {
          setTopicIndex(currentTopicIndex + 1);
          setSubtopicIndex(0);
        } else if (currentLayerIndex < data.layers.length - 1) {
          setLayerIndex(currentLayerIndex + 1);
          setTopicIndex(0);
          setSubtopicIndex(0);
        } else {
          console.log('Questionnaire completed');
          navigate('/score', { state: { responses, jsonData: data } });
        }
      }
    }
  };

  return (
    <div>
      <h2>Layer: {layer.name}</h2>
      <h3>Topic: {topic.name}</h3>
      <h4>Subtopic: {subtopic.name}</h4>
      <h5>Section: {section.name}</h5>

      <div>
        {section.questions.map((question, qid) => (
          <div key={qid} style={{ marginBottom: '20px' }}>
            <p><strong>{question.name}</strong></p>
            {question.info && <p>{question.info}</p>}

            <p><strong>Good Point (if Yes):</strong> {question.good_point}</p>
            <p><strong>Scope for Improvement (if No):</strong> {question.scope_for_improvement}</p>

            {question.tags && <p><strong>Tags:</strong> {question.tags.join(', ')}</p>}

            {/* Options - Assuming yes/no options for simplicity */}
            <div>
              <button onClick={() => handleResponseChange(question.name, 'Yes') & handleNext('Yes')} style={{ marginRight: '10px' }}>Yes</button>
              <button onClick={() => handleResponseChange(question.name, 'No') & handleNext('No')} style={{ marginRight: '10px' }}>No</button>
            </div>
          </div>
        ))}
      </div>

      <button onClick={() => handleNext()}>Next</button>
    </div>
  );
}

export default QuestionnairePage;
