import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/AuthPage.css";
import { auth } from "../firebase/firebase.js"; // Import auth instance
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
} from "firebase/auth";

function AuthPage() {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    name: "",
    organization: "",
  });
  const [error, setError] = useState("");
  const navigate = useNavigate(); // Set up navigation

  const toggleAuthMode = () => {
    setIsLogin(!isLogin);
    setError(""); // Clear any previous error messages
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { email, password, name, organization } = formData;

    try {
      if (isLogin) {
        // Login logic
        await signInWithEmailAndPassword(auth, email, password);
        console.log("Login successful");
        navigate("/admin"); // Redirect to the admin page
      } else {
        // Signup logic
        const userCredential = await createUserWithEmailAndPassword(
          auth,
          email,
          password
        );

        // Additional user info can be stored in Firestore if needed
        console.log("Signup successful:", {
          userId: userCredential.user.uid,
          name,
          organization,
        });

        navigate("/admin"); // Redirect to the admin page
      }
    } catch (err) {
      setError(err.message); // Display error to the user
      console.error("Auth error:", err);
    }
  };

  return (
    <div className="auth-page">
      <h2>{isLogin ? "Login" : "Signup"}</h2>
      <form onSubmit={handleSubmit}>
        {!isLogin && (
          <>
            <input
              type="text"
              name="name"
              placeholder="Name"
              onChange={handleInputChange}
            />
            <input
              type="text"
              name="organization"
              placeholder="Organization"
              onChange={handleInputChange}
            />
          </>
        )}
        <input
          type="email"
          name="email"
          placeholder="Email"
          onChange={handleInputChange}
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          onChange={handleInputChange}
        />
        <button type="submit">{isLogin ? "Login" : "Signup"}</button>
        {error && <p style={{ color: "red" }}>{error}</p>}
        <button className="toggle-button" type="button" onClick={toggleAuthMode}>
          {isLogin ? "Go to Signup" : "Go to Login"}
        </button>
      </form>
    </div>
  );
}

export default AuthPage;
