import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/Questionnaire.css";
import { db } from "../firebase/firebase.js"; // Import Firestore instance
import { collection, doc, setDoc, getDocs } from "firebase/firestore";

function QuestionnairePage() {
  const [data, setData] = useState(null);
  const [currentLayerIndex, setLayerIndex] = useState(0);
  const [currentTopicIndex, setTopicIndex] = useState(0);
  const [currentSubtopicIndex, setSubtopicIndex] = useState(0);
  const [currentSectionIndex, setSectionIndex] = useState(0);
  const [responses, setResponses] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const fetchDataFromFirestore = async () => {
      try {
        const layersCollection = collection(db, "layers");
        const layersSnapshot = await getDocs(layersCollection);

        if (layersSnapshot.empty) {
          console.log("Firestore is empty. Uploading data...");
          await uploadInitialData();
        }

        const firestoreData = await getFirestoreData(layersSnapshot);
        setData(firestoreData); // Load data from Firestore
      } catch (error) {
        console.error("Error fetching Firestore data:", error);
      }
    };

    fetchDataFromFirestore();
  }, []);

  // Function to upload initial data to Firestore
  const uploadInitialData = async (jsonData) => {
    try {
      const layers = jsonData.layers;

      for (const layer of layers) {
        const layerRef = doc(collection(db, "layers"), layer.name);
        await setDoc(layerRef, { name: layer.name });

        for (const topic of layer.topics) {
          const topicRef = doc(collection(layerRef, "topics"), topic.tname);
          await setDoc(topicRef, { name: topic.tname });

          for (const subtopic of topic.subtopics) {
            const sanitizedSubtopicName = subtopic.stname.replace(/[^a-zA-Z0-9]/g, '_');
            const subtopicRef = doc(collection(topicRef, "subtopics"), sanitizedSubtopicName);
            await setDoc(subtopicRef, { name: subtopic.stname });

            // Check if sections exist
            if (subtopic.sections && subtopic.sections.length > 0) {
              for (const section of subtopic.sections) {
                const sectionRef = doc(collection(subtopicRef, "sections"), section.sname);
                await setDoc(sectionRef, { name: section.sname });

                // Check if questions exist
                if (section.questions && section.questions.length > 0) {
                  for (const question of section.questions) {
                    const questionRef = doc(collection(sectionRef, "questions"), question.name);
                    await setDoc(questionRef, {
                      name: question.name,
                      info: question.info || "",
                      good_point: question.good_point || "",
                      scope_for_improvement: question.scope_for_improvement || "",
                      mandatory: question.mandatory || false,
                      tags: question.tags || [],
                    });
                  }
                } else {
                  console.warn(`No questions found for section: ${section.sname}`);
                }
              }
            } else {
              console.warn(`No sections found for subtopic: ${subtopic.stname}`);
            }
          }
        }
      }

      console.log("Data successfully added to Firestore!");
    } catch (error) {
      console.error("Error adding data to Firestore:", error);
    }
  };


  // Function to fetch data from Firestore
  const getFirestoreData = async (layersSnapshot) => {
    const layers = [];
    for (const layerDoc of layersSnapshot.docs) {
      const topicsSnapshot = await getDocs(collection(layerDoc.ref, "topics"));
      const topics = [];
      for (const topicDoc of topicsSnapshot.docs) {
        const subtopicsSnapshot = await getDocs(collection(topicDoc.ref, "subtopics"));
        const subtopics = [];
        for (const subtopicDoc of subtopicsSnapshot.docs) {
          const sectionsSnapshot = await getDocs(collection(subtopicDoc.ref, "sections"));
          const sections = [];
          for (const sectionDoc of sectionsSnapshot.docs) {
            const questionsSnapshot = await getDocs(collection(sectionDoc.ref, "questions"));
            const questions = questionsSnapshot.docs.map((doc) => doc.data());
            sections.push({ name: sectionDoc.id, questions });
          }
          subtopics.push({ name: subtopicDoc.id, sections });
        }
        topics.push({ name: topicDoc.id, subtopics });
      }
      layers.push({ name: layerDoc.id, topics });
    }
    return { layers };
  };

  // Navigate through layers, topics, subtopics, and sections
  const handleNext = () => {
    if (currentSectionIndex < data.layers[currentLayerIndex].topics[currentTopicIndex].subtopics[currentSubtopicIndex].sections.length - 1) {
      setSectionIndex((prev) => prev + 1);
    } else if (currentSubtopicIndex < data.layers[currentLayerIndex].topics[currentTopicIndex].subtopics.length - 1) {
      setSectionIndex(0);
      setSubtopicIndex((prev) => prev + 1);
    } else if (currentTopicIndex < data.layers[currentLayerIndex].topics.length - 1) {
      setSectionIndex(0);
      setSubtopicIndex(0);
      setTopicIndex((prev) => prev + 1);
    } else if (currentLayerIndex < data.layers.length - 1) {
      setSectionIndex(0);
      setSubtopicIndex(0);
      setTopicIndex(0);
      setLayerIndex((prev) => prev + 1);
    } else {
      console.log("Questionnaire completed!");
      navigate("/score", { state: { responses, jsonData: data } });
    }
  };

  if (!data) return <div>Loading...</div>;

  const layer = data.layers[currentLayerIndex];
  const topic = layer.topics[currentTopicIndex];
  const subtopic = topic.subtopics[currentSubtopicIndex];
  const section = subtopic.sections[currentSectionIndex];

  const handleResponseChange = (questionName, answer) => {
    setResponses((prevResponses) => ({
      ...prevResponses,
      [questionName]: answer,
    }));
  };

  return (
    <div className="panel">
      <div className="panel-header">
        <h2>Layer: {layer.name}</h2>
        <h3>Topic: {topic.name}</h3>
        <h4>Subtopic: {subtopic.name}</h4>
        <h5>Section: {section.name}</h5>
      </div>
      <div className="panel-body">
        {section.questions.map((question, index) => (
          <div key={index} className="question-panel">
            <p><strong>{question.name}</strong></p>
            <div className="buttons">
              <button onClick={() => handleResponseChange(question.name, "Yes")}>Yes</button>
              <button onClick={() => handleResponseChange(question.name, "No")}>No</button>
            </div>
          </div>
        ))}
      </div>
      <button className="next-button" onClick={handleNext}>
        Next
      </button>
    </div>
  );
}

