import React, { useState } from 'react';

import dummyCredentials from '../config';

function AuthPage() {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    name: '',
    organization: '',
    contactEmail: ''
  });
  const [error, setError] = useState('');

  const navigate = useNavigate(); // Set up navigation

  const toggleAuthMode = () => {
    setIsLogin(!isLogin);
    setError(''); // Clear any previous error messages
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (isLogin) {
      // Login logic: Check against dummy credentials
      if (formData.username === dummyCredentials.username && formData.password === dummyCredentials.password) {
        console.log("Login successful");
        // Redirect or load another component, etc.
      } else {
        setError("Invalid username or password");
      }
    } else {
      // Signup logic: Implement the required functionality
      console.log("Signup data:", formData);
      // Clear form or provide feedback to the user after signup
    }
  };

  return (
    <div>
      <h2>{isLogin ? 'Login' : 'Signup'}</h2>
      <form onSubmit={handleSubmit}>
        {!isLogin && (
          <>
            <input
              type="text"
              name="name"
              placeholder="Name"
              onChange={handleInputChange}
            />
            <input
              type="text"
              name="organization"
              placeholder="Organization"
              onChange={handleInputChange}
            />
            <input
              type="email"
              name="contactEmail"
              placeholder="Contact Email"
              onChange={handleInputChange}
            />
          </>
        )}
        <input
          type="text"
          name="username"
          placeholder="Username"
          onChange={handleInputChange}
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          onChange={handleInputChange}
        />
        <button type="submit">{isLogin ? 'Login' : 'Signup'}</button>
        {error && <p style={{ color: 'red' }}>{error}</p>}
      </form>
      <button onClick={toggleAuthMode}>
        {isLogin ? 'Go to Signup' : 'Go to Login'}
      </button>
    </div>
  );
}

export default AuthPage;
