// src/components/QuestionnairePage.js
import React, { useState, useEffect } from 'react';

function QuestionnairePage() {
  const [data, setData] = useState(null);
  const [currentLayerIndex, setLayerIndex] = useState(0);
  const [currentTopicIndex, setTopicIndex] = useState(0);
  const [currentSubtopicIndex, setSubtopicIndex] = useState(0);

  useEffect(() => {
    fetch('/questionnaireData.json')
      .then(response => response.json())
      .then(data => setData(data))
      .catch(error => console.error("Error loading JSON data:", error));
  }, []);

  if (!data) return <div>Loading...</div>;

  const layer = data.layers[currentLayerIndex];
  const topic = layer.topics[currentTopicIndex];
  const subtopic = topic.subtopics[currentSubtopicIndex];

  const handleNext = () => {
    // Update indices here to move to the next subtopic or topic
    const currentLayer = data.layers[currentLayerIndex];
    const currentTopic = currentLayer.topics[currentTopicIndex];
    const currentSubtopic = currentTopic.subtopics[currentSubtopicIndex];

    // Check if there are more subtopics in the current topic
    if (currentSubtopicIndex < currentTopic.subtopics.length - 1) {
      setSubtopicIndex(currentSubtopicIndex + 1);
    } 
    // If there are no more subtopics, check if there are more topics in the current layer
    else if (currentTopicIndex < currentLayer.topics.length - 1) {
      setSubtopicIndex(0); // reset subtopic index
      setTopicIndex(currentTopicIndex + 1);
    } 
    // If there are no more topics, check if there are more layers
    else if (currentLayerIndex < data.layers.length - 1) {
      setSubtopicIndex(0); // reset subtopic index
      setTopicIndex(0); // reset topic index
      setLayerIndex(currentLayerIndex + 1);
    } 
    // If we've reached the last subtopic of the last topic in the last layer, optionally handle completion
    else {
      console.log('Questionnaire completed');
    }
  };

  return (
    <div>
      <h2>Layer: {data.layers[currentLayerIndex].name}</h2>
      <h3>Topic: {data.layers[currentLayerIndex].topics[topicIndex].name}</h3>
      <h4>Subtopic: {data.layers[layerIndex].topics[topicIndex].subtopics[subtopicIndex].name}</h4>
      <div>
        {data.layers[layerIndex].topics[topicIndex].subtopics[subtopicIndex].questions.map((question, i) => (
          <p key={i}>{question}</p>
        ))}
      </div>
      <button onClick={handleNext}>Next</button>
    </div>
  );
  // return (
  //   <div>
  //     <h2>{layer.name}</h2>
  //     <h3>{topic.name}</h3>
  //     <h4>{subtopic.name}</h4>
  //     {subtopic.questions.map((q, idx) => (
  //       <div key={q.id}>
  //         <p>{q.question}</p>
  //         <button>Yes</button>
  //         <button>No</button>
  //       </div>
  //     ))}
  //     <button onClick={handleNext}>Next</button>
  //   </div>
  // );
}

export default QuestionnairePage;
