import React, { useState, useEffect } from 'react';

function QuestionnairePage() {
  const [data, setData] = useState(null);
  const [currentLayerIndex, setLayerIndex] = useState(0);
  const [currentTopicIndex, setTopicIndex] = useState(0);
  const [currentSubtopicIndex, setSubtopicIndex] = useState(0);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  useEffect(() => {
    fetch('/questionnaireData.json')
      .then(response => response.json())
      .then(data => setData(data))
      .catch(error => console.error("Error loading JSON data:", error));
  }, []);

  if (!data) return <div>Loading...</div>;

  const layer = data.layers[currentLayerIndex];
  const topic = layer.topics[currentTopicIndex];
  const subtopic = topic.subtopics[currentSubtopicIndex];
  // const currentQuestion = subtopic.questions[currentQuestionIndex];


  const handleNext = () => {
    // Update indices here to move to the next subtopic or topic
    const currentLayer = data.layers[currentLayerIndex];
    const currentTopic = currentLayer.topics[currentTopicIndex];
    const currentSubtopic = currentTopic.subtopics[currentSubtopicIndex];
    // Check if there are more questions in the current subtopic
