    //   setCurrentQuestionIndex(0);
    //   setSubtopicIndex(0);
    //   setTopicIndex(0);
    //   setLayerIndex(currentLayerIndex + 1);
    // } 
    // // If reached the end of all layers, handle completion
    // else {
    //   console.log("Questionnaire completed");
    // }

    // Check if there are more subtopics in the current topic
    if (currentSubtopicIndex < currentTopic.subtopics.length - 1) {
      setSubtopicIndex(currentSubtopicIndex + 1);
    } 
    // If there are no more subtopics, check if there are more topics in the current layer
    else if (currentTopicIndex < currentLayer.topics.length - 1) {
      setSubtopicIndex(0); // reset subtopic index
      setTopicIndex(currentTopicIndex + 1);
    } 
    // If there are no more topics, check if there are more layers
    else if (currentLayerIndex < data.layers.length - 1) {
      setSubtopicIndex(0); // reset subtopic index
      setTopicIndex(0); // reset topic index
      setLayerIndex(currentLayerIndex + 1);
    } 
    // If we've reached the last subtopic of the last topic in the last layer, optionally handle completion
    else {
      console.log('Questionnaire completed');
    }
  };

  // return (
